
public class ArraysExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] numb = {20,30,40,50,60};
		System.out.println("Total number of values are: " + numb.length);
		System.out.println("Value at 3rd position is: " + numb[2]);
		
		String name = "Clean Tanzania Green Tanzania";
		String [] arrWords = name.split(" ");
		System.out.println("Value at 3rd position is: " + arrWords[2]);
		System.out.println("Total number of Words are: " + arrWords.length);

	}

}
